const eventEmitter = new events.EventEmitter()
eventEmitter.on('tap', () => {
    console.log('tap event')
})

eventEmitter.emit('tap')
eventEmitter.on('open', () => {
    console.log('reading')
})
eventEmitter.emit('open')
