console.log(fs.readFileSync('./sample.txt').toString()) // <Buffer 68 65 6c 6c 6f 20 77 6f 72 6c 64 21>
var data  = ''
const stream = fs.createReadStream('sample.txt', 'utf8');
stream.on('data', function(chunk) {
    data += chunk;
}).on('end', function() {
    console.log(data);
});





